sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }


    g_form.clearMessages();


    switch (newValue) {
        case '1': 
            g_form.showFieldMsg('u_freq', 'Take one dose every 24 hours.', 'info');
            break;

        case '2': 
            g_form.showFieldMsg('u_freq', 'Take one dose every 12 hours.', 'info');
            break;

        case '3': 
            g_form.showFieldMsg('u_freq', 'Take one dose every 8 hours.', 'info');
            break;

        default:
            g_form.showFieldMsg('u_freq', 'Please select a valid frequency.', 'info');
            break;
    }
}
