sys_script_client.script:
function onLoad() {
    if (!g_form.isNewRecord()) {

        // To check whether a patient is a VIP or not
        var vipStatus = g_form.getValue('u_vip');
        var gaPatientUtils = new GlideAjax('global.PatientUtilsClient');
        gaPatientUtils.addParam('sysparm_name', 'checkIfVIP');
        gaPatientUtils.addParam('sysparm_vipStatus', vipStatus);
        gaPatientUtils.getXMLAnswer(checkVip);

        // To fetch patient details by patient Id
        var patientID = g_form.getValue('u_number');
        var gaPatientUtils2 = new GlideAjax('global.PatientUtilsClient');
        gaPatientUtils2.addParam('sysparm_name', 'fetchPatientDetails');
        gaPatientUtils2.addParam('sysparm_patientID', patientID);
        gaPatientUtils2.getXMLAnswer(getPatDetails);

    }
function getPatDetails(patDetails) {
        var res = JSON.parse(patDetails);
        if (res.error) {
            g_form.addErrorMessage(res.error);
        } else {
            g_form.addInfoMessage("First Name: " + res.firstName + ", Last Name: " + res.lastName);
        }
    }

    function checkVip(vipStatus) {
        var res1 = JSON.parse(vipStatus);
        g_form.addInfoMessage(res1);
    }

}
