sys_script_client.script:
function onLoad() {

    var patientId = g_form.getValue('u_patient_id'); 

    if (!patientId) return;

    // Call the Script Include
    var ga = new GlideAjax('PatientUtilsClient');
    ga.addParam('sysparm_name', 'getPatientDetails');
    ga.addParam('sysparm_patient_id', patientId);

    ga.getXMLAnswer(function(response) {
        if (response) {
            var data = JSON.parse(response);

            // Set values in form fields
            g_form.setValue('u_first_name', data.name);     
            g_form.setValue('u_date_birth', data.dob);
            g_form.setValue('u_gender', data.gender);
        }
    });
}

}
