sys_script_include.script:
var InsuranceUtils = Class.create();
InsuranceUtils.prototype = {
    initialize: function() {
	},

    // a. Fetch insurance details by patient ID
    getInsuranceByPatient: function(patientId) {
        var result = [];
        var insGR = new GlideRecord('u_insurance'); 
        insGR.addQuery('u_patient_id', patientId); 
        insGR.query();

        while (insGR.next()) {
            result.push({
                insurance_id: insGR.getUniqueValue(),
                provider: insGR.getDisplayValue('u_provider'),
                policy_number: insGR.getDisplayValue('u_policy_number'),
                status: insGR.getDisplayValue('u_status')
            });
        }

        return result;
    },

    // b. Check if an insurance policy is active
    isPolicyActive: function(policyId) {
        var insGR = new GlideRecord('u_insurance');
        insGR.addQuery('number', policyId);
        insGR.query();

        if (insGR.next()) {
            var start = new GlideDate();
            start.setValue(insGR.getValue('start_date'));

            var end = new GlideDate();
            end.setValue(insGR.getValue('end_date'));

            var today = new GlideDate();

            return today.onOrAfter(start) && today.onOrBefore(end);
        }

        return false;
    },

    // c. Fetch insurance policy by ID
    getInsuranceById: function(policyId) {
        var insGR = new GlideRecord('u_insurance');
        insGR.addQuery('number', policyId);
        insGR.query();

        if (insGR.next()) {
            return {
                insurance_id: insGR.getUniqueValue(),
                patient: insGR.getDisplayValue('u_patient_id'),
                provider: insGR.getDisplayValue('u_provider'),
                policy_number: insGR.getDisplayValue('u_policy_number'),
                start_date: insGR.getDisplayValue('start_date'),
                end_date: insGR.getDisplayValue('end_date'),
                status: insGR.getDisplayValue('u_status')
            };
        }

        return null;
    },

    // d. Fetch insurance policies by provider
    getPoliciesByProvider: function(providerId) {
        var result = [];
        var insGR = new GlideRecord('u_insurance');
        insGR.addQuery('provider', providerId);
        insGR.query();

        while (insGR.next()) {
            result.push({
                insurance_id: insGR.getUniqueValue(),
                patient: insGR.getDisplayValue('patient'),
                policy_number: insGR.getDisplayValue('policy_number'),
                start_date: insGR.getDisplayValue('start_date'),
                end_date: insGR.getDisplayValue('end_date'),
                status: insGR.getDisplayValue('status')
            });
        }

        return result;
    },



    type: 'InsuranceUtils'
};
