sys_script_include.script:
var BillingUtils = Class.create();
BillingUtils.prototype = {
    initialize: function() {
    },

    // a. Calculate the total billing amount for a patient
    getTotalBillingAmount: function(patientId) {
        var total = 0;
        var billingGR = new GlideRecord('u_billing'); 
        billingGR.addQuery('u_patient_id', patientId); 
        billingGR.query();

        while (billingGR.next()) {
            var amount = parseFloat(billingGR.getValue('u_total_amount')); 
            if (!isNaN(amount)) {
                total += amount;
            }
        }

        return total;
    },

    // b. Check if a billing record is overdue
    isBillingOverdue: function(billId) {
        var billingGR = new GlideRecord('u_billing');
        billingGR.addQuery('number', billId);
        billingGR.query();

        if (billingGR.next()) {
            var dueDate = new GlideDate();
            dueDate.setValue(billingGR.getValue('u_due_date')); 
            var today = new GlideDate();

            return today.after(dueDate); 
        }

        return false; 
    },

    // c. Fetch billing details by ID
    getBillingDetailsById: function(billId) {
        var billingGR = new GlideRecord('u_billing');
        billingGR.addQuery('number', billId);
        billingGR.query();

        if (billingGR.next()) {
            return {
                billing_id: billingGR.getUniqueValue(),
                patient: billingGR.getDisplayValue('u_patient_id'),
                amount: billingGR.getDisplayValue('u_total_amount'),
                status: billingGR.getDisplayValue('u_status'),


            };
        }

        return null;
    },

    // d. Fetch billing records by patient ID
    getBillingRecordsByPatient: function(patientId) {
        var result = [];
        var billingGR = new GlideRecord('u_billing');
        billingGR.addQuery('u_patient_id', patientId);
        billingGR.query();

        while (billingGR.next()) {
            result.push({
                billing_id: billingGR.getUniqueValue(),
                amount: billingGR.getDisplayValue('u_total_amount'),
                status: billingGR.getDisplayValue('u_status'),

            });
        }

        return result;
    },


    type: 'BillingUtils'
};
