sys_script_include.script:
var PatientUtilsClass = Class.create();
PatientUtilsClass.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


    fetchPatientDetails: function() {
        var patientID = this.getParameter('sysparm_patientID');
        var patientGr = new GlideRecord('u_patient');
        patientGr.addQuery('number', patientID);
        patientGr.query();

        var record = {};
        if (patientGr.next()) {
            record.firstName = patientGr.u_first_name.toString();
            record.lastName = patientGr.u_last_name.toString();
        } else {
            record.error = "no patient found";
        }
        return JSON.stringify(record);
    },
    checkIfVIP: function() {
        var vipStat = this.getParameter('sysparm_vipStatus');
        if (vipStat) {
            return true;
        } else {
            return false;
        }

    },

    type: 'PatientUtilsClient'
});
