sys_script_include.script:
var LabTestUtils = Class.create();
LabTestUtils.prototype = {
    initialize: function() {
    },
    // a. Fetch lab test results by test ID
    getLabTestResultById: function(testId) {
        var labGR = new GlideRecord('u_lab_test'); 
        labGR.addQuery('number', testId);
        labGR.query();

        if (labGR.next()) {
            return {
                lab_test_id: labGR.getUniqueValue(),
                patient: labGR.getDisplayValue('u_patient_id'),
                test_type: labGR.getDisplayValue('u_test_type'),
                result: labGR.getDisplayValue('u_result'),
                status: labGR.getDisplayValue('u_status'),

            };
        }

        return null; 
    },

    // b. Check if lab test results are ready
    isResultReady: function(testId) {
        var labGR = new GlideRecord('u_lab_test');
        labGR.addQuery('number', testId);
        labGR.query();

        if (labGR.next()) {
            var status = labGR.getValue('u_status'); 
            return status == 'ready' || status == 'Ready' || status == 'completed';
        }

        return false;
    },

    // c. Fetch lab tests by patient ID
    getLabTestsByPatient: function(patientId) {
        var result = [];
        var labGR = new GlideRecord('u_lab_test');
        labGR.addQuery('u_patient_id', patientId);
        labGR.query();

        while (labGR.next()) {
            result.push({
                lab_test_id: labGR.getUniqueValue(),
                test_name: labGR.getDisplayValue('u_test_type'),
                result: labGR.getDisplayValue('u_result'),
                status: labGR.getDisplayValue('u_status'),
                date: labGR.getDisplayValue('u_schedule_date')
            });
        }

        return result;
    },

    // d. Fetch lab tests by date
    getLabTestsByDate: function(dateStr) {
        var result = [];
        var labGR = new GlideRecord('u_lab_test');
        labGR.addQuery('test_date', 'STARTSWITH', dateStr); 
        labGR.query();

        while (labGR.next()) {
            result.push({
                lab_test_id: labGR.getUniqueValue(),
                patient: labGR.getDisplayValue('u_patient_id'),
                test_name: labGR.getDisplayValue('u_test_type'),
                result: labGR.getDisplayValue('u_result'),
                status: labGR.getDisplayValue('u_status')

            });
        }

        return result;
    },


    type: 'LabTestUtils'
};
