var MedicationUtils = Class.create();
MedicationUtils.prototype = {
    initialize: function() {
    },
    // a. Calculate the total dosage for a medication
    calculateTotalDosage: function(medicationId) {
        var medGR = new GlideRecord('u_medication'); 
        medGR.addQuery('number', medicationId);
        medGR.query();

        if (medGR.next()) {
            var dosage = parseFloat(medGR.getValue('u_dosage'));           
            var frequency = parseInt(medGR.getValue('u_frequency'), 10);   

            if (!isNaN(dosage) && !isNaN(frequency) && !isNaN(duration)) {
                var total = dosage * frequency ;
                return total; 
            }
        }

        return null; // Medication not found or invalid fields
    },


    type: 'MedicationUtils'
};
