sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

	(function runInboundEmailAction() {
    var emailBody = email.body_text;


    var patientIdMatch = emailBody.match(/Patient ID:\s*(PAT\d+)/i);
    var medicationNameMatch = emailBody.match(/Medication Name:\s*(.+)/i);
    var dosageMatch = emailBody.match(/Dosage:\s*(.+)/i);
    var frequencyMatch = emailBody.match(/Frequency:\s*(.+)/i);


    if (!patientIdMatch || !medicationNameMatch || !dosageMatch || !frequencyMatch) {
        gs.log("Medication Refill Request: Missing required data in email body.");
        return;
    }

    var patientId = patientIdMatch[1].trim();
    var medicationName = medicationNameMatch[1].trim();
    var dosage = dosageMatch[1].trim();
    var frequency = frequencyMatch[1].trim();


    var medGR = new GlideRecord('u_medication');
    medGR.initialize();
    medGR.u_patient_id = patientId; 
    medGR.u_medication_name = medicationName;
    medGR.u_dosage = dosage;
    medGR.u_freq = frequency;
    medGR.u_start_date = new GlideDateTime();
    medGR.u_status = 'Prescribed'; 
    medGR.insert();

    gs.print("Medication Refill Request: New record created for patient ID " + patientId);
})();

})(current, event, email, logger, classifier);
