sysevent_in_email_action.script:
(function runAction(current, event, email, logger, classifier) {
  try {
    var emailBody = email.body_text;

    var patientIdMatch = emailBody.match(/Patient Id\s*:\s*(PAT\d+)/i);
    var medNameMatch = emailBody.match(/Medication Name\s*:\s*(.+)/i);
    var dosageMatch = emailBody.match(/Dosage\s*:\s*(.+)/i);
    var frequencyMatch = emailBody.match(/Frequency\s*:\s*(.+)/i);

    if (!patientIdMatch || !medNameMatch || !dosageMatch || !frequencyMatch) {
      gs.error("Medication refill data missing. Email Body: " + emailBody);
      return;
    }

    var patientId = patientIdMatch[1];
    var medName = medNameMatch[1].trim();
    var dosage = dosageMatch[1].trim();
    var frequency = frequencyMatch[1].trim();

    var patient = new GlideRecord('u_patient');
    patient.addQuery('number', patientId);
    patient.query();

    if (!patient.next()) {
      gs.error("Patient not found for ID: " + patientId);
      return;
    }

    var newMed = new GlideRecord('u_medication');
    newMed.initialize();
    newMed.u_patient_id = patient.sys_id;
    newMed.u_medication_name = medName;
    newMed.u_dosage = dosage;
    newMed.u_freq = frequency;
    newMed.u_start_date = new GlideDateTime();
    newMed.u_status = 'requested';
    newMed.insert();

    gs.info("Medication refill created for Patient ID: " + patientId);

  } catch (e) {
    gs.error("Error processing medication refill: " + e.message + "\nStack: " + e.stack);
  }
})(current, event, email, logger, classifier);
