sysevent_in_email_action.script:
(function runAction(current, event, email, logger, classifier) {
    try {
        var emailBody = email.body_text || "";


        var patientIdMatch = emailBody.match(/Patient ID:\s*(\w+)/i); // Example: PAT12345
        var contactNumberMatch = emailBody.match(/New Contact Number:\s*(.+)/i);
        var newEmailMatch = emailBody.match(/New Email:\s*(.+@.+)/i);

        // Validate all fields
        if (!patientIdMatch || !contactNumberMatch || !newEmailMatch) {
            gs.error("Missing required fields. Email body:\n" + emailBody);
            return;
        }

        var patientId = patientIdMatch[1].trim();
        var newContactNumber = contactNumberMatch[1].trim();
        var newEmail = newEmailMatch[1].trim();

        gs.info(" Patient ID: " + patientId);
        gs.info(" New Contact Number: " + newContactNumber);
        gs.info("New Email: " + newEmail);


        var patientGR = new GlideRecord('u_patient');
        patientGR.addQuery('number', patientId); 
        patientGR.query();

        if (patientGR.next()) {
            // Update fields
            patientGR.u_contact_number = newContactNumber;
            patientGR.u_email = newEmail;
            patientGR.update();
            gs.info(" Patient record updated successfully for ID: " + patientId);
        } else {
            gs.error( "No patient found with u_number = " + patientId);
        }
    } catch (e) {
        gs.error("Script error: " + e.message + "\n" + e.stack);
    }
})(null, event, email, logger, classifier);
